import Header from './components/Header';
import Footer from './components/Footer';
import RoomGrid from './components/RoomGrid';
import BookingForm from './components/BookingForm';
import Controls from './components/Controls';
import { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [rooms, setRooms] = useState([]);
  const [bookingResult, setBookingResult] = useState(null);

  const fetchRooms = async () => {
    const res = await axios.get('http://localhost:5000/api/rooms');
    setRooms(res.data);
  };

  useEffect(() => {
    fetchRooms();
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-purple-100 text-gray-800 font-sans">
      <Header />
      <main className="max-w-5xl mx-auto p-4">
        <BookingForm setBookingResult={setBookingResult} fetchRooms={fetchRooms} />
        <Controls fetchRooms={fetchRooms} />

        {bookingResult && (
          <div className={`mt-4 p-3 rounded shadow-lg ${
            bookingResult.success ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
          }`}>
            {bookingResult.success
              ? `✅ Booked Rooms: ${bookingResult.rooms.map(r => r.number).join(', ')} | Travel Time: ${bookingResult.time} min`
              : `❌ ${bookingResult.message}`}
          </div>
        )}

        <RoomGrid rooms={rooms} />
      </main>
      <Footer />
    </div>
  );
}

export default App;
