import axios from 'axios';
const Controls = ({ fetchRooms }) => {
  const handleRandom = async () => {
    await axios.post('http://localhost:5000/api/random');
    fetchRooms();
  };
  const handleReset = async () => {
    await axios.post('http://localhost:5000/api/reset');
    fetchRooms();
  };
  return (
    <div className="flex gap-4 mb-4">
      <button onClick={handleRandom} className="bg-yellow-500 text-white px-3 py-1 rounded">Random Occupancy</button>
      <button onClick={handleReset} className="bg-red-600 text-white px-3 py-1 rounded">Reset Booking</button>
    </div>
  );
};
export default Controls;
