import Header from './components/Header';
import Footer from './components/Footer';
import RoomGrid from './components/RoomGrid';
import BookingForm from './components/BookingForm';
import Controls from './components/Controls';
import PreviewResult from './components/PreviewResult';
import Admin from './pages/Admin';
import AdminLogin from './pages/AdminLogin';
import { useEffect, useState } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

function App() {
  const [rooms, setRooms] = useState([]);
  const [bookingResult, setBookingResult] = useState(null);
  const [preview, setPreview] = useState(null);
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
  const [selectedType, setSelectedType] = useState('AC');
  const [isAdmin, setIsAdmin] = useState(localStorage.getItem('admin-auth') === 'true');

  const fetchRooms = async () => {
    const res = await axios.get('http://localhost:5000/api/rooms', {
      params: { date: selectedDate }
    });
    setRooms(res.data);
  };

  useEffect(() => {
    fetchRooms();
  }, [selectedDate]);

  const confirmBooking = async () => {
    const res = await axios.post('http://localhost:5000/api/book', {
      count: preview.rooms.length,
      date: selectedDate,
      type: selectedType
    });
    setBookingResult(res.data);
    setPreview(null);
    fetchRooms();
  };

  return (
    <Router>
      <Routes>
        <Route path="/admin-login" element={<AdminLogin setIsAdmin={setIsAdmin} />} />
        <Route
          path="/admin"
          element={isAdmin ? <Admin /> : <Navigate to="/admin-login" replace />}
        />
        <Route
          path="/"
          element={
            <div className="min-h-screen bg-gradient-to-br from-blue-50 to-purple-100 text-gray-800 font-sans flex flex-col">
              <Header />

              <main className="flex-grow max-w-5xl mx-auto p-6 bg-white rounded-2xl shadow-xl mt-6 mb-6 transition-all">
                <div className="text-center mb-6">
                  <h2 className="text-3xl font-extrabold text-indigo-700 mb-2">
                    üè® Hotel Room Reservation
                  </h2>
                  <p className="text-gray-600 text-sm md:text-base">
                    Select your preferred date, type, and number of rooms. Enjoy smart booking with optimized travel time!
                  </p>
                </div>

                <div className="mb-6">
                  <BookingForm
                    setBookingResult={setBookingResult}
                    fetchRooms={fetchRooms}
                    setPreview={setPreview}
                    selectedDate={selectedDate}
                    setSelectedDate={setSelectedDate}
                    selectedType={selectedType}
                    setSelectedType={setSelectedType}
                  />
                </div>

                {preview && (
                  <div className="mb-6">
                    <PreviewResult preview={preview} onConfirm={confirmBooking} />
                  </div>
                )}

                {bookingResult && (
                  <div
                    className={`mb-6 p-4 rounded-xl text-center font-medium text-lg transition duration-300 ease-in-out shadow ${
                      bookingResult.success
                        ? 'bg-green-100 text-green-800'
                        : 'bg-red-100 text-red-700'
                    }`}
                  >
                    {bookingResult.success
                      ? `‚úÖ Booked Rooms: ${bookingResult.rooms.map(r => r.number).join(', ')} | Travel Time: ${bookingResult.time} min`
                      : `‚ùå ${bookingResult.message}`}
                  </div>
                )}

                <RoomGrid rooms={rooms.filter(r => r.type === selectedType)} />
              </main>

              <Footer />
            </div>
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
