import Header from './components/Header';
import Footer from './components/Footer';
import RoomGrid from './components/RoomGrid';
import BookingForm from './components/BookingForm';
import Controls from './components/Controls';
import PreviewResult from './components/PreviewResult';
import { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [rooms, setRooms] = useState([]);
  const [bookingResult, setBookingResult] = useState(null);
  const [preview, setPreview] = useState(null);

  const fetchRooms = async () => {
    const res = await axios.get('http://localhost:5000/api/rooms');
    setRooms(res.data);
  };

  useEffect(() => {
    fetchRooms();
  }, []);

  const confirmBooking = async () => {
    const res = await axios.post('http://localhost:5000/api/book', {
      count: preview.rooms.length
    });
    setBookingResult(res.data);
    setPreview(null);
    fetchRooms();
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-purple-100 text-gray-800 font-sans">
      <Header />
      <main className="max-w-5xl mx-auto p-6 bg-white rounded-xl shadow-lg mt-6">
        <h2 className="text-2xl font-bold text-center text-indigo-700 mb-6">
          üìã Book Your Hotel Rooms
        </h2>

        <BookingForm setBookingResult={setBookingResult} fetchRooms={fetchRooms} setPreview={setPreview} />
        {preview && <PreviewResult preview={preview} onConfirm={confirmBooking} />}

        {bookingResult && (
          <div
            className={`mt-4 p-4 rounded-lg text-center font-medium text-lg transition-all duration-300 shadow-md ${
              bookingResult.success
                ? 'bg-green-100 text-green-800'
                : 'bg-red-100 text-red-700'
            }`}
          >
            {bookingResult.success
              ? `‚úÖ Booked Rooms: ${bookingResult.rooms.map(r => r.number).join(', ')} | Travel Time: ${bookingResult.time} min`
              : `‚ùå ${bookingResult.message}`}
          </div>
        )}

        <RoomGrid rooms={rooms} />
      </main>
      <Footer />
    </div>
  );
}

export default App;
