import { useState } from 'react';
import axios from 'axios';
const BookingForm = ({ setBookingResult, fetchRooms }) => {
  const [count, setCount] = useState(1);
  const handleSubmit = async () => {
    const res = await axios.post('http://localhost:5000/api/book', { count });
    setBookingResult(res.data);
    fetchRooms();
  };
  return (
    <div className="flex items-center gap-4 mb-4">
      <input type="number" min={1} max={5} value={count} onChange={e => setCount(Number(e.target.value))} className="border px-2 py-1 w-20" />
      <button onClick={handleSubmit} className="bg-blue-600 text-white px-4 py-1 rounded">Book Rooms</button>
    </div>
  );
};
export default BookingForm;
